const chunks = [];
const chunkSize = 1000;
const chunkOverlap = 200;
const text = $('Extract from File').item.json.text.replace(/\n/, '');

for (let i=0,j=Math.round(text.length/chunkSize)+1;i<j;i++) {
  chunks.push(
    text.substr(
      Math.max(0,(i * chunkSize)-chunkOverlap),
      chunkSize
    )
  );
}

return { chunks };

